package Geekster_Test;

public class BeginningEnd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j=99,k;
		System.out.println("1 number from beginning and 1 number from end:");
		for(i=1;i<=100;i++)
		{
			k=i+j;
			j=j-2;
			System.out.print(""+i+","+k+",");
			
		}

	}

}
